properties.script:
parseEndPoint = $('EndPoint').split("/api/")[1];

parseEndPoint = parseEndPoint.split("/");

channelMap.put('uriFragment1', validate(parseEndPoint[0], '', new Array()));

channelMap.put('uriFragment2', validate(parseEndPoint[1], '', new Array()));

channelMap.put('uriFragment3', validate(parseEndPoint[2], '', new Array()));

channelMap.put('uriFragment4', validate(parseEndPoint[3], '', new Array()));

channelMap.put('uriFragment5', validate(parseEndPoint[4], '', new Array()));

switch (String($('uriFragment1')))
{
	case 'countries'://	api/countries
		switch (String($('uriFragment2')))
		{
			case ''://	api/countries/
				switch (String($('Http Method')))
				{
					case 'get':// Get api/countries
						channelMap.put('singularResourceName', validate('Country', '', new Array()));
						channelMap.put('pluralResourceName', validate('Countries', '', new Array()));
						channelMap.put('Soap Payload', validate(getXmlPayload(1), '', new Array()));
						break;
				}
				break;
			default:// api/countries/{countryName}
				switch (String($('uriFragment3'))) {
					case '':// api/countries/{countryName}/
						break;
					case 'holidays': //	api/countries/{countryName}/holidays
						switch (String($('uriFragment4')))
						{
							case '':
								switch (String($('Http Method')))
								{
									case 'get':// Get api/countries/{countryName}/holidays/
										channelMap.put('singularResourceName', validate('Holiday', '', new Array()));
										channelMap.put('pluralResourceName', validate('Holidays', '', new Array()));
										payloadData = {'countryCode': $('uriFragment2').toString()};
										channelMap.put('Soap Payload', validate(getXmlPayload(2, payloadData), '', new Array()));	
										break;
								}
								break;
							default:
								switch (String($('Http Method')))
								{
									case 'get':// Get api/countries/{countryName}/holidays/{holidayName}	
										channelMap.put('singularResourceName', validate('Holiday', '', new Array()));
										channelMap.put('pluralResourceName', validate('Holidays', '', new Array()));
										payloadData = {'countryCode': $('uriFragment2').toString(),
													'holidayCode': $('uriFragment4').toString()};

										var extraRequest = [];
										extraRequest[0] = {'require': 'holidayName', 'countryCode': $('uriFragment2'), 'holidayCode': $('uriFragment4')};

										channelMap.put('extraRequest', validate(extraRequest, '', new Array()));									
										channelMap.put('payload', validate(payloadData, '', new Array()));
										channelMap.put('Soap Payload', validate(getXmlPayload(3, payloadData), '', new Array()));	
										break;
								}
								break;
					}
					break;
				}
				break;
		}
		break;
}

for (var i = 0; i < $('extraRequest').length; i++)
{
	flagFirst = true;
	json = 	'{';

	for (var key in $('extraRequest')[i])
	{
		if (!flagFirst)
		{
			json = json + ', ';
		} else flagFirst = false;

		json = json + '"'  + key + '" : "' + ($('extraRequest')[i])[key] + '"';					
	}
	json = json + '}';

	var response = router.routeMessage('Holiday Sender Slave', json);

	$('extraRequest')[i]['result'] = JSON.parse(response.getMessage());
}

com.mirth.connect.plugins.javascriptstep.script:
//static values
	tmp['status'] = "success";
	tmp['code'] = 200;
	tmp['message'] = "Ok";
//

//dynamic values
	tmp['resource'] = $('singularResourceName');

	tmp['total'] = msg.*::['Body'].*::['Get' + $('pluralResourceName') + 'AvailableResponse'].*::['Get' + $('pluralResourceName') + 'AvailableResult'].*::[$('singularResourceName') + 'Code'].length();

	tmp['total'] = msg.*::['Body'].*::['Get' + $('pluralResourceName') + 'AvailableResponse'].*::['Get' + $('pluralResourceName') + 'AvailableResult'].*::[$('singularResourceName') + 'Code'].length();

	//collection
	if (tmp['total'] > 0)
	{
		tmp['data'] = [];

		for (var i = 0; i < msg.*::['Body'].*::['Get' + $('pluralResourceName') + 'AvailableResponse'].*::['Get' + $('pluralResourceName') +'AvailableResult'].*::[$('singularResourceName') + 'Code'].length(); i++)
		{
			tmp['data'][i] = {};
			tmp['data'][i]['code'] = msg.*::['Body'].*::['Get' + $('pluralResourceName') + 'AvailableResponse'].*::['Get' + $('pluralResourceName') + 'AvailableResult'].*::[$('singularResourceName') + 'Code'][i].*::['Code'].toString();
			tmp['data'][i]['description'] = msg.*::['Body'].*::['Get' + $('pluralResourceName') + 'AvailableResponse'].*::['Get' + $('pluralResourceName') + 'AvailableResult'].*::[$('singularResourceName') + 'Code'][i].*::['Description'].toString();

			for (var index in $('extraRequest'))
			{
				for (var key in $('extraRequest')[index])
				{
					tmp['data'][i][key] = $('extraRequest')[index][key];
				}
			}
		}
	}
	else
	{
		tmp['data'] = {};

		tmp['total']++;

		tmp['data']['code'] = String($('uriFragment4'));

		tmp['data']['date'] = msg.*::['Body'].*::['GetHolidayDateResponse'].*::['GetHolidayDateResult'].toString();

		for (var index in $('extraRequest'))
		{
			for (var key in $('extraRequest')[index]['result'])
			{
				tmp['data'][key] = $('extraRequest')[index]['result'][key];
			}
		}
	}

