properties.code:
/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getXmlPayload(payloadID, payloadData)
{
	switch (payloadID)
	{
		case 1: 
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetCountriesAvailable xmlns='http://www.holidaywebservice.com/HolidayService_v2/' />"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";

			break;
		case 2:
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetHolidaysAvailable xmlns='http://www.holidaywebservice.com/HolidayService_v2/'>"
      			+ "<countryCode>" + payloadData["countryCode"] + "</countryCode>"
    				+ "</GetHolidaysAvailable>"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";

			break;
		case 3:
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetHolidayDate xmlns='http://www.holidaywebservice.com/HolidayService_v2/'>"
      			+ "<countryCode>" + payloadData["countryCode"] + "</countryCode>"
      			+ "<holidayCode>" + payloadData["holidayCode"] + "</holidayCode>"
      			+ "<year>" + new Date().getFullYear() + "</year>"
   	 			+ "</GetHolidayDate>"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";
		default:
			return "";	
	}
}

com.mirth.connect.plugins.javascriptstep.script:
switch (String($('require')))
{
	case "holidayName":
		channelMap.put('Holiday Code', validate(msg['holidayCode'], new Array()));
		channelMap.put('Soap Payload', validate(getXmlPayload(2,msg), new Array()));
		break;
	default:
		channelMap.put('Soap Payload', validate("", '', new Array()));	
}

var senderSlaveResult = {};

switch (String($('require')))
{
	case "holidayName":

		for (var i = 0; i < msg.*::['Body'].*::['GetHolidaysAvailableResponse'].*::['GetHolidaysAvailableResult'].*::['HolidayCode'].length(); i++)
		{	
			if (String($('Holiday Code')).equals(msg.*::['Body'].*::['GetHolidaysAvailableResponse'].*::['GetHolidaysAvailableResult'].*::['HolidayCode'][i].*::['Code'].toString()))
			{
				tmp['holidayName'] = msg.*::['Body'].*::['GetHolidaysAvailableResponse'].*::['GetHolidaysAvailableResult'].*::['HolidayCode'][i].*::['Description'];
			}
		}

		break;
}

globalMap.put('Sender Slave Result', validate(senderSlaveResult, '', new Array()));

