/**
	Modify the description here. Modify the function name and parameters as needed. One function per
	template is recommended; create a new code template for each new function.

	@param {String} arg1 - arg1 description
	@return {String} return description
*/
function getXmlPayload(payloadID, payloadData)
{
	switch (payloadID)
	{
		case 1: 
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetCountriesAvailable xmlns='http://www.holidaywebservice.com/HolidayService_v2/' />"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";

			break;
		case 2:
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetHolidaysAvailable xmlns='http://www.holidaywebservice.com/HolidayService_v2/'>"
      			+ "<countryCode>" + payloadData["countryCode"] + "</countryCode>"
    				+ "</GetHolidaysAvailable>"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";

			break;
		case 3:
			return "<?xml version='1.0' encoding='utf-8'?>"
				+ "<soap12:Envelope xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns:soap12='http://www.w3.org/2003/05/soap-envelope'>"
  				+ "<soap12:Body>"
    				+ "<GetHolidayDate xmlns='http://www.holidaywebservice.com/HolidayService_v2/'>"
      			+ "<countryCode>" + payloadData["countryCode"] + "</countryCode>"
      			+ "<holidayCode>" + payloadData["holidayCode"] + "</holidayCode>"
      			+ "<year>" + new Date().getFullYear() + "</year>"
   	 			+ "</GetHolidayDate>"
  				+ "</soap12:Body>"
				+ "</soap12:Envelope>";
		default:
			return "";	
	}
}
